import Vue from 'vue';
import Vuex from 'vuex';
import clients from "@/store/modules/clients";

Vue.use(Vuex)

export default new Vuex({
    modules: {
        clients
    }
})

new Vue({
    render: h => h(App),
    store // Передача Vuex store в экземпляр Vue
}).$mount('#app');
