import InfoProducts from "@/components/products/InfoProducts";
export default {
  name: "ProductList",
  components: {
    InfoProducts
  },
  data() {
    return {
      baseUrl: 'https://dummyjson.com/products',
      array: []
    };
  },
  methods: {
    changeProductData(newProductsData) {
      let index = this.array.findIndex(i => i.id === newProductsData['id']);
      let data = [newProductsData.title, newProductsData.price];
      axios.put('https://dummyjson.com/product/' + newProductsData.id, {
        data
      });
      this.array[index] = newProductsData;
    }
  },
  async created() {
    try {
      const {
        status,
        data
      } = await axios.get(this.baseUrl);
      if (status === 200) {
        this.array = data.products;
      }
    } catch (e) {
      console.log(e.response);
    }
  },
